mapper.select:
SELECT a.cdSeq ,a.name ,a.codeGroup_seq ,a.cdRegDate ,a.cdUpdate ,a.cdDelny ,(select aa.name from codegroup aa where 1=1 and aa.cgSeq = a.codeGroup_seq) as codegroup_name FROM code a WHERE 1=1
AND a.cdDelny = #{shcdDelny}
AND a.cdRegDate BETWEEN #{shDateStart} AND #{shDateEnd} AND a.cdUpdate BETWEEN #{shDateStart} AND #{shDateEnd}
AND a.codeGroup_seq = #{shValue} AND a.cdSeq = #{shValue} AND a.name LIKE CONCAT('%',#{shValue},'%')
ORDER BY a.cdSeq DESC

mapper.update:

UPDATE code
set
name = #{name}
WHERE cdSeq = #{cdSeq}
