mapper.sql:
         FROM hotellist a
         LEFT JOIN roomdetail b on a.hotelSeq = b.roomSeq
         LEFT JOIN hotelbooking c on b.roomSeq = c.bookingSeq
        WHERE 1=1
        	
        	
        	AND a.hotelTypeCD = #{hotelTypeCD}
        	AND a.areaCD = #{areaCD} 
        	
        	
        	AND a.areaCD = #{shAreaCD}
