mapper.sql:
         FROM member a
        WHERE 1=1

        	AND a.mbDelny = #{shDelny} 


					AND a.mbRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.mbUpdate BETWEEN #{shDateStart} AND #{shDateEnd}


			AND a.genderCD = #{shGenderCD} 


            AND a.memberSeq = #{shValue}
            AND a.fristName LIKE CONCAT('%',#{shValue},'%')
            AND a.lastName LIKE CONCAT('%',#{shValue},'%')
            AND a.birthday LIKE CONCAT('%',#{shValue},'%')
            AND a.memberID LIKE CONCAT('%',#{shValue},'%')
            AND a.memberPhone LIKE CONCAT('%',#{shValue},'%')

mapper.update:

UPDATE member 
set
fristName =	#{fristName}
,lastName = #{lastName}
,genderCD = #{genderCD}
,birthday = #{birthday}
,memberID = #{memberID}
,memberPassword = #{memberPassword}
,memberPhone = #{memberPhone}
,codeNumber = #{codeNumber}
,roadName = #{roadName}
,addrDetail = #{addrDetail}
WHERE memberSeq = #{memberSeq}
